package Thema4Schleifen;

public class CountdownProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean bedingung = false;

		while (bedingung) {
			System.out.println("Hello World");
			int ablauf = 0;
			ablauf++;

			if (ablauf == 10)
				break;
		}

	}

}
